var elem=document.getElementById("mySwipe");window.mySwipe=Swipe(elem,{continuous:!1,disableScroll:!0,callback:function(e,i){$(".movie-nav li").removeClass("active");var a=mySwipe.getPos()+1;$(".movie-nav li:nth-child("+a+")").addClass("active");var t=$(".movie-container:nth-child("+a+") a").attr("href");$(".view-more").attr("href",t)}});var apiURL="data.json";new Vue({el:"#app",data:{currentBranch:"dev",items:null},created:function(){this.fetchData()},methods:{fetchData:function(){var e=this;$.get(apiURL,function(i){var a=i.slice(0,5);e.items=a})}}}),$(".movie-nav li").click(function(){$(".movie-nav li").removeClass("active");var e=$(".movie-nav li").index(this);mySwipe.slide(e,400),$(this).addClass("active")}),$(".view-more").attr("href",$(".movie-container:nth-child(1) a").attr("href"));